// Generate exercises array for a given topic
function genExercises(title, count = 40) {
  const exercises = [];
  for (let i = 0; i < count; i++) {
    exercises.push(genQuestionByTopic(title, i));
  }
  return exercises;
}


// Ensure all topics have slugs
Object.keys(grammarData).forEach(level => {
  grammarData[level].forEach(topic => {
    if (!topic.slug) {
      topic.slug = toSlug(topic.title);
    }
  });
});

// Utility function to get topic by slug
function getTopicBySlug(slug) {
  for (let level in grammarData) {
    const topic = grammarData[level].find(t => t.slug === slug);
    if (topic) {
      return { ...topic, level };
    }
  }
  return null;
}

